INSERT INTO `boke_article` VALUES ('253', '枚举', '2', 'enum.ordinal()方法', '海职院小天', '2020-05-24 18:53:40', '3b5ce49c-1661-4ccd-8d4f-b3684e9c3f15.jpg', '<h2 style=\"font-weight:normal;font-family:Helvetica, Arial, sans-serif;font-size:1.6em;color:#53555C;background-color:#FFFFFF;text-indent:2em;\">\n	描述\n</h2>\n<p style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#4D4D4D;background-color:#FFFFFF;text-align:justify;text-indent:2em;\">\n	<span style=\"font-weight:700;font-family:;\">java.lang.Enum.ordinal()</span>方法返回此枚举常量的序数（其枚举声明中的位置，其中初始常量分配的序数为零）。\n</p>\n<h2 style=\"font-weight:normal;font-family:Helvetica, Arial, sans-serif;font-size:1.6em;color:#53555C;background-color:#FFFFFF;text-indent:2em;\">\n	声明\n</h2>\n<p style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#4D4D4D;background-color:#FFFFFF;text-align:justify;text-indent:2em;\">\n	以下是java.lang.Enum.ordinal()方法的声明\n</p>\n<p style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#4D4D4D;background-color:#FFFFFF;text-align:justify;text-indent:2em;\">\n	<span color:#000088;\"=\"\" style=\"font-family: &quot;sans serif&quot;, tahoma, verdana, helvetica; font-size: 12px;\">public</span><span style=\"font-family:&quot;font-size:12px;\"> </span><span color:#000088;\"=\"\" style=\"font-family: &quot;sans serif&quot;, tahoma, verdana, helvetica; font-size: 12px;\">final</span><span style=\"font-family:&quot;font-size:12px;\"> </span><span color:#000088;\"=\"\" style=\"font-family: &quot;sans serif&quot;, tahoma, verdana, helvetica; font-size: 12px;\">int</span><span style=\"font-family:&quot;font-size:12px;\"> ordinal</span><span color:#666600;\"=\"\" style=\"font-family: &quot;sans serif&quot;, tahoma, verdana, helvetica; font-size: 12px;\">()</span>\n</p>\n<p>\n	<br />\n</p>\n<h2 style=\"font-weight:normal;font-family:Helvetica, Arial, sans-serif;font-size:1.6em;color:#53555C;background-color:#FFFFFF;text-indent:2em;\">\n	参数\n</h2>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>\n<ul style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#535B60;background-color:#FFFFFF;\">\n	<li style=\"font-family:;\">\n		<p style=\"color:#4D4D4D;text-align:justify;\">\n			<span style=\"font-weight:700;\">NA</span> \n		</p>\n	</li>\n</ul>\n<p>\n	<br />\n</p>\n<h2 style=\"font-weight:normal;font-family:Helvetica, Arial, sans-serif;font-size:1.6em;color:#53555C;background-color:#FFFFFF;text-indent:2em;\">\n	返回值\n</h2>\n<p style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#4D4D4D;background-color:#FFFFFF;text-align:justify;text-indent:2em;\">\n	此方法返回的枚举常量的序数。\n</p>\n<h2 style=\"font-weight:normal;font-family:Helvetica, Arial, sans-serif;font-size:1.6em;color:#53555C;background-color:#FFFFFF;text-indent:2em;\">\n	异常\n</h2>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>\n<ul style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#535B60;background-color:#FFFFFF;\">\n	<li style=\"font-family:;\">\n		<p style=\"color:#4D4D4D;text-align:justify;\">\n			<span style=\"font-weight:700;\">NA</span> \n		</p>\n	</li>\n</ul>\n<p>\n	<br />\n</p>\n<h2 style=\"font-weight:normal;font-family:Helvetica, Arial, sans-serif;font-size:1.6em;color:#53555C;background-color:#FFFFFF;text-indent:2em;\">\n	实例\n</h2>\n<p style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#4D4D4D;background-color:#FFFFFF;text-align:justify;text-indent:2em;\">\n	下面的例子说明了如何使用java.lang.Enum.ordinal()方法.\n</p>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>\n<pre class=\"prettyprint\">&nbsp;&nbsp;&nbsp;&nbsp;package com.yiibai;\n\n&nbsp;&nbsp;&nbsp;&nbsp;import java.lang.*;\n \n&nbsp;&nbsp;&nbsp;&nbsp;// enum showing Mobile prices\n&nbsp;&nbsp;&nbsp;&nbsp;enum Mobile {\n   &nbsp;&nbsp;&nbsp;&nbsp;Samsung(400), Nokia(250),Motorola(325);\n  \n   &nbsp;&nbsp;&nbsp;&nbsp;int price;\n   &nbsp;&nbsp;&nbsp;&nbsp;Mobile(int p) {\n      &nbsp;&nbsp;&nbsp;&nbsp;price = p;\n   &nbsp;&nbsp;&nbsp;&nbsp;}\n   &nbsp;&nbsp;&nbsp;&nbsp;int showPrice() {\n      &nbsp;&nbsp;&nbsp;&nbsp;return price;\n   &nbsp;&nbsp;&nbsp;&nbsp;} \n&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;public class EnumDemo {\n\n   &nbsp;&nbsp;&nbsp;&nbsp;public static void main(String args[]) {\n\n    &nbsp;&nbsp;&nbsp;&nbsp; System.out.println(\"CellPhone List:\");\n     for(Mobile m : Mobile.values()) {\n        System.out.println(m + \" costs \" + m.showPrice() + \" dollars\");\n     }\n\n     Mobile ret = Mobile.Samsung;\n     System.out.println(\"The ordinal is = \" + ret.ordinal());\n     System.out.println(\"MobileName = \" + ret.name());                      \n   }\n}</pre>\n<p>\n	<br />\n</p>\n<p style=\"font-family:Helvetica, Arial, sans-serif;font-size:16px;color:#4D4D4D;background-color:#FFFFFF;text-align:justify;text-indent:2em;\">\n	编译和运行上面的程序，将产生以下结果：\n</p>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>\n<pre class=\"prettyprint\"> CellPhone List:\n Samsung costs 400 dollars\n Nokia costs 250 dollars\n Motorola costs 325 dollars\n The ordinal is = 0\n MobileName = Samsung</pre>\n<p>\n	<br />\n</p>', '', '0', '1', '0');
INSERT INTO `boke_article` VALUES ('254', '将数字组成的字符转化为int型数据', '0', 'Integer.parseInt（String s）', '海职院小天', '2020-05-24 19:44:28', '1229d32f-4a97-4bcd-806e-810103956d74.jpeg', '<p style=\"font-size:12pt;text-indent:2em;\">\n	Integer.parseInt(String.valueOf());\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-size:12pt;text-indent:2em;\">\n	Integer.parseInt(String s) 可以将提取好的“数字”组成的字符转化为int型数据。类似的，可以yongByte.short.long.Float.Double类调用相应的类方法。\n</p>\n<p style=\"margin-left:0.375in;font-size:12pt;text-indent:2em;\">\n	String.valueIOf()可以将上述的数据类型转化为String对象。\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	以下是例子\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	package\ncn.com;\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	import\njava.util.Scanner;\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	<span>//</span><span>要求自定义输入数字</span><span> </span><span>输出相应数字的拼音</span>\n</p>\n<p style=\"font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	<span>//</span><span>列如</span><span> </span><span>输入</span><span> 56 </span><span>输出</span><span> wu liu </span><span>输入</span><span>-89 </span><span>输出</span><span> fu ba jiu</span>\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	public\nclass S {\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	public static void main(String[] args) {\n</p>\n<p style=\"margin-left:0.375in;font-size:12pt;text-indent:2em;\">\n	//创建字符串数组 字符串的元素为1-9的拼音\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	String[] number = {\"ling\", \"yi\", \"er\",\n\"san\", \"si\", \"wu\", \"liu\",\n\"qi\", \"ba\", \"jiu\"};\n</p>\n<p style=\"margin-left:0.75in;font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	<span>//Scanner </span><span>自定义输入</span><span> </span><span>字符</span><span> </span><span>s</span>\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	Scanner sc = new Scanner(System.in);\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	String s = sc.next();\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	for (int i = 0; i &lt; s.length(); i++) {\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.75in;font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	<span>//</span><span>当输入的字符串的第一个字符为</span><span>“-”</span><span>时，输出</span><span> “fu”</span>\n</p>\n<p style=\"margin-left:1.125in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	if (s.charAt(i) == \'-\') {\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	System.out.print(\"fu \");\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	else {\n</p>\n<p style=\"margin-left:1.5in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	int t =\nInteger.parseInt(String.valueOf(s.charAt(i)));\n</p>\n<p style=\"margin-left:1.5in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:1.5in;font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	<span>//</span><span>输出number数组对应下标的元素</span>\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	System.out.print(number[t]);\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:1.5in;font-family:&quot;font-size:12.0pt;text-indent:2em;\">\n	<span>//</span><span>如果i不等于s</span><span> </span><span>数组的长度</span><span> - 1 </span><span>那么输出“</span><span> </span><span>”</span>\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	if (i !=\ns.length()-1) {\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	System.out.print(\" \");\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"font-family:Calibri;font-size:12pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>', '', '0', '1', '0');
INSERT INTO `boke_article` VALUES ('255', '返回字符串中某个位置的字符', '1', 'java方法', '海职院小天', '2020-05-24 21:39:08', '05bdc662-41d3-4eac-8858-54426dd000f2.jpg', '<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<span>char</span><span>A</span><span>t（）方法</span>\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	charAt（）方法用于返回指定索引处的字符。\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<span>索引范围为从</span><span>0</span><span>到length（）</span><span>-1.</span>\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	语法：\n</p>\n<p style=\"margin-left:0.375in;font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<span>Public char charAt</span><span>（int</span><span> index</span><span>）</span>\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	参数\n</p>\n<p style=\"margin-left:0.375in;font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<span>Index --</span><span>字符的索引</span>\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	返回值\n</p>\n<p style=\"margin-left:0.375in;font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	返回指定索引处的字符\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	实例：\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	package\ncn.com;\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	<span>public class </span><span>Main</span><span>{</span>\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	public\nstatic void main(String[] args){\n</p>\n<p style=\"margin-left:0.75in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.75in;font-size:11pt;text-indent:2em;\">\n	//输入字符串\"I\nLove GPLT\"\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	String a = \"I Love GPLT\";\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.75in;font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<span>//</span><span>循环输出字符串a的字符</span>\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	for (int i=0; i&lt;a.length(); i++) {\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"margin-left:0.375in;font-size:11pt;text-indent:2em;\">\n	//charAt()\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	System.out.println(a.charAt(i));\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.375in;font-family:Calibri;font-size:11pt;text-indent:2em;\">\n	}\n</p>\n<p style=\"margin-left:0.375in;font-family:&quot;font-size:11.0pt;text-indent:2em;\">\n	<br />\n</p>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>', '', '0', '1', '0');
